package codegen

const fileSourceTmpl = `
{{- define "File:Source" -}}
{{- UseWire -}}
// WARNING: This file is machine generated by fidlgen.

#include <{{ .Library | GeneratedPath "cpp/wire.h" }}>
#include <memory>
{{ "" }}



{{- range .Decls }}
{{- if Eq .Kind Kinds.Const }}{{ template "Const:Source" . }}{{- end }}
{{- if Eq .Kind Kinds.Protocol }}{{ $protocol := . }}
{{- range $transport, $_ := .Transports }}{{- if eq $transport "Channel" -}}
{{ template "Protocol:Source" $protocol }}
{{ template "Protocol:EventSender:Source" $protocol }}
{{- end }}{{ end }}{{ end }}
{{- if Eq .Kind Kinds.Struct }}{{ template "Struct:Source" . }}{{- end }}
{{- if Eq .Kind Kinds.Union }}{{ template "Union:Source" . }}{{- end }}
{{- if Eq .Kind Kinds.Table }}{{ template "Table:Source" . }}{{- end }}
{{- end }}
{{ "" }}

{{ EndOfFile }}
{{ end }}
`
